function createCameraTween(endPos, endTaget) {
  new TWEEN.Tween({
    // 相机初始的坐标
    x: camera.position.x,
    y: camera.position.y,
    z: camera.position.z,
    // 目标初始坐标
    tx: controls.target.x,
    ty: controls.target.y,
    tz: controls.target.z,
  })
    .to({
      x: endPos.x,
      y: endPos.y,
      z: endPos.z,
      tx: endTaget.x,
      ty: endTaget.y,
      tz: endTaget.z,
    })
    .onUpdate((e) => {
      camera.position.set(e.x, e.y, e.z);
      camera.lookAt(e.tx, e.ty, e.tz);
      controls.target.set(e.tx, e.ty, e.tz);
      controls.update();
    })
    .start();
}
